import React, { useState } from 'react';

function App() {
  const [file, setFile] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [error, setError] = useState('');
  const [isDragging, setIsDragging] = useState(false);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile?.type === 'application/pdf') {
      setFile(droppedFile);
      setError('');
      mockParseFile(droppedFile);
    } else {
      setError('Please upload a valid PDF file');
    }
  };

  const handleFileSelect = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setError('');
      mockParseFile(selectedFile);
    }
  };

  const mockParseFile = (file) => {
    setTimeout(() => {
      setTransactions([
        { date: '01/01/2024', description: 'Opening Balance', debit: 0, credit: 50000, balance: 50000 },
        { date: '05/01/2024', description: 'Payment to Vendor ABC', debit: 15000, credit: 0, balance: 35000 },
        { date: '10/01/2024', description: 'Customer Payment Received', debit: 0, credit: 25000, balance: 60000 },
        { date: '15/01/2024', description: 'Salary Payment', debit: 30000, credit: 0, balance: 30000 },
      ]);
    }, 500);
  };

  const handleConvertToTally = () => {
    alert('Converting to Tally XML...');
  };

  const handleExportToExcel = () => {
    alert('Exporting to Excel/CSV...');
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      {/* Header */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-gradient-to-br from-primary to-accent rounded-lg flex items-center justify-center">
              <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
            </div>
            <h1 className="text-2xl font-bold text-gray-900">Ladgerit</h1>
          </div>
          <nav className="hidden md:flex space-x-6">
            <a href="#" className="text-gray-600 hover:text-primary transition">Home</a>
            <a href="#" className="text-gray-600 hover:text-primary transition">Features</a>
            <a href="#" className="text-gray-600 hover:text-primary transition">Support</a>
          </nav>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 max-w-7xl w-full mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Sidebar - Workflow Steps */}
          <aside className="lg:w-64 flex-shrink-0">
            <div className="bg-white rounded-lg shadow-sm p-6 sticky top-8">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Workflow</h2>
              <div className="space-y-4">
                {[
                  { step: 1, label: 'Upload', icon: 'ðŸ“¤', active: !file },
                  { step: 2, label: 'Preview', icon: 'ðŸ‘ï¸', active: file && !transactions.length },
                  { step: 3, label: 'Convert', icon: 'âš™ï¸', active: transactions.length > 0 },
                  { step: 4, label: 'Download', icon: 'â¬‡ï¸', active: false },
                ].map((item) => (
                  <div key={item.step} className={`flex items-center space-x-3 p-3 rounded-lg ${item.active ? 'bg-primary text-white' : 'bg-gray-50 text-gray-600'}`}>
                    <span className="text-2xl">{item.icon}</span>
                    <div>
                      <div className="text-sm font-medium">{item.label}</div>
                      <div className="text-xs opacity-75">Step {item.step}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </aside>

          {/* Main Section */}
          <div className="flex-1 space-y-6">
            {/* Upload Area */}
            <div className="bg-white rounded-lg shadow-sm p-8">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Upload Bank Statement</h2>
              <div
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
                className={`border-2 border-dashed rounded-xl p-12 text-center transition ${
                  isDragging ? 'border-primary bg-blue-50' : 'border-gray-300 hover:border-primary'
                }`}
              >
                <svg className="mx-auto h-16 w-16 text-gray-400 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
                </svg>
                <p className="text-lg font-medium text-gray-700 mb-2">Upload your PDF Bank Statement</p>
                <p className="text-sm text-gray-500 mb-4">Drag and drop or click to browse</p>
                <input
                  type="file"
                  accept=".pdf"
                  onChange={handleFileSelect}
                  className="hidden"
                  id="file-upload"
                />
                <label
                  htmlFor="file-upload"
                  className="inline-block bg-primary text-white px-6 py-3 rounded-lg font-medium cursor-pointer hover:bg-blue-700 transition shadow-md"
                >
                  Select PDF File
                </label>
                {file && (
                  <div className="mt-4 text-sm text-gray-600">
                    âœ“ Selected: <span className="font-medium">{file.name}</span>
                  </div>
                )}
              </div>

              {/* Validation Results */}
              {error && (
                <div className="mt-4 bg-red-50 border border-red-200 rounded-lg p-4 flex items-start space-x-3">
                  <svg className="w-5 h-5 text-red-500 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                  </svg>
                  <div>
                    <h3 className="text-sm font-medium text-red-800">Validation Error</h3>
                    <p className="text-sm text-red-700 mt-1">{error}</p>
                  </div>
                </div>
              )}
            </div>

            {/* Transaction Preview Table */}
            {transactions.length > 0 && (
              <>
                <div className="bg-white rounded-lg shadow-sm overflow-hidden">
                  <div className="p-6 border-b border-gray-200">
                    <h2 className="text-xl font-semibold text-gray-900">Transaction Preview</h2>
                    <p className="text-sm text-gray-500 mt-1">{transactions.length} transactions found</p>
                  </div>
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Description</th>
                          <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Debit</th>
                          <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Credit</th>
                          <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Balance</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {transactions.map((tx, idx) => (
                          <tr key={idx} className="hover:bg-gray-50">
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{tx.date}</td>
                            <td className="px-6 py-4 text-sm text-gray-900">{tx.description}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-right text-red-600 font-medium">
                              {tx.debit > 0 ? `â‚¹${tx.debit.toLocaleString()}` : '-'}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-right text-green-600 font-medium">
                              {tx.credit > 0 ? `â‚¹${tx.credit.toLocaleString()}` : '-'}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-right text-gray-900 font-medium">
                              â‚¹{tx.balance.toLocaleString()}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>

                {/* Conversion Actions */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h2 className="text-xl font-semibold text-gray-900 mb-4">Export Options</h2>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <button
                      onClick={handleConvertToTally}
                      className="bg-accent text-white px-8 py-4 rounded-lg font-semibold text-lg hover:bg-orange-600 transition shadow-lg hover:shadow-xl flex items-center justify-center space-x-2"
                    >
                      <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7H5a2 2 0 00-2 2v9a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2h-3m-1 4l-3 3m0 0l-3-3m3 3V4" />
                      </svg>
                      <span>Convert to Tally XML</span>
                    </button>
                    <button
                      onClick={handleExportToExcel}
                      className="bg-primary text-white px-8 py-4 rounded-lg font-semibold text-lg hover:bg-blue-700 transition shadow-lg hover:shadow-xl flex items-center justify-center space-x-2"
                    >
                      <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 10v6m0 0l-3-3m3 3l3-3m2 8H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                      </svg>
                      <span>Export to Excel/CSV</span>
                    </button>
                  </div>
                </div>

                {/* Summary & Download */}
                <div className="bg-gradient-to-r from-primary to-accent rounded-lg shadow-lg p-6 text-white">
                  <h3 className="text-lg font-semibold mb-3">Summary</h3>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div>
                      <div className="text-sm opacity-90">Total Transactions</div>
                      <div className="text-2xl font-bold">{transactions.length}</div>
                    </div>
                    <div>
                      <div className="text-sm opacity-90">Total Debits</div>
                      <div className="text-2xl font-bold">â‚¹{transactions.reduce((sum, tx) => sum + tx.debit, 0).toLocaleString()}</div>
                    </div>
                    <div>
                      <div className="text-sm opacity-90">Total Credits</div>
                      <div className="text-2xl font-bold">â‚¹{transactions.reduce((sum, tx) => sum + tx.credit, 0).toLocaleString()}</div>
                    </div>
                    <div>
                      <div className="text-sm opacity-90">Closing Balance</div>
                      <div className="text-2xl font-bold">â‚¹{transactions[transactions.length - 1]?.balance.toLocaleString()}</div>
                    </div>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex flex-col md:flex-row justify-between items-center text-sm text-gray-600">
            <p>Â© 2024 Ladgerit. All rights reserved.</p>
            <p className="mt-2 md:mt-0">Powered by Replit</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
