import React, { useState } from 'react';
import { trackFileUpload, trackConversion, trackError } from './utils/analytics';

function App() {
  const [file, setFile] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [metadata, setMetadata] = useState({});
  const [error, setError] = useState('');
  const [isDragging, setIsDragging] = useState(false);
  const [loading, setLoading] = useState(false);
  const [password, setPassword] = useState('');
  const [showPasswordPrompt, setShowPasswordPrompt] = useState(false);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile?.type === 'application/pdf') {
      setFile(droppedFile);
      setError('');
      setPassword('');
      setShowPasswordPrompt(false);
    } else {
      setError('Please upload a valid PDF file');
    }
  };

  const handleFileSelect = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      // File validation
      if (selectedFile.size > 50 * 1024 * 1024) { // 50MB limit
        setError('File size too large. Maximum 50MB allowed.');
        return;
      }
      if (selectedFile.type !== 'application/pdf') {
        setError('Please select a valid PDF file.');
        return;
      }
      setFile(selectedFile);
      setError('');
      setPassword('');
      setShowPasswordPrompt(false);
    }
  };

  const uploadFile = async () => {
    if (!file) return;

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('file', file);
    if (password) formData.append('password', password);

    try {
      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        if (response.status === 401 || (data.error && (data.error.includes('password') || data.error.includes('encrypted')))) {
          setShowPasswordPrompt(true);
          setError('PDF is password protected. Please enter password below and submit again.');
        } else {
          setError(data.error || 'Failed to parse PDF');
        }
        setLoading(false);
        return;
      } else {
        setTransactions(data.transactions || []);
        setMetadata(data.metadata || {});
        setShowPasswordPrompt(false);
      }
    } catch (err) {
      setError('Cannot connect to server. Please ensure the API server is running on port 5000.');
      setTransactions([]);
    } finally {
      setLoading(false);
    }
  };

  const handleConvertToTally = async () => {
    const bankLedger = prompt('Enter Bank Ledger Name (as in Tally):');
    if (!bankLedger) return;

    try {
      const response = await fetch('http://localhost:5000/api/export/tally', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ transactions, bankLedger }),
      });

      if (!response.ok) throw new Error('Export failed');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `tally_${bankLedger}_${new Date().toISOString().split('T')[0]}.xml`;
      a.click();
    } catch (err) {
      alert('Failed to export: ' + err.message);
    }
  };

  const handleExportToExcel = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/export/csv', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ transactions }),
      });

      if (!response.ok) throw new Error('Export failed');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `transactions_${new Date().toISOString().split('T')[0]}.csv`;
      a.click();
    } catch (err) {
      alert('Failed to export: ' + err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 flex flex-col">
      {/* Header */}
      <header className="bg-white shadow-md sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-3 sm:py-4 flex items-center justify-between">
          <div className="flex items-center space-x-3 sm:space-x-4">
            <img src="/logo.png" alt="Ledgerit Logo" className="w-16 h-16 sm:w-20 sm:h-20 lg:w-24 lg:h-24" />
            <h1 className="text-2xl sm:text-3xl lg:text-4xl font-bold bg-gradient-to-r from-primary to-accent bg-clip-text text-transparent">Ledgerit</h1>
          </div>
          <nav className="hidden md:flex space-x-8">
            <a href="#home" className="text-gray-700 hover:text-primary transition font-medium">Home</a>
            <a href="#features" className="text-gray-700 hover:text-primary transition font-medium">Features</a>
            <a href="#support" className="text-gray-700 hover:text-primary transition font-medium">Support</a>
          </nav>
          <button className="md:hidden p-2">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </header>

      {/* Hero Section */}
      <section className="bg-gradient-to-r from-primary to-accent py-12 sm:py-16 lg:py-20 text-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex flex-col lg:flex-row items-center gap-8 lg:gap-12">
            <div className="flex-1 space-y-4 sm:space-y-6 text-center lg:text-left">
              <h2 className="text-3xl sm:text-4xl lg:text-5xl font-bold leading-tight">Convert PDF Bank Statements to Tally XML in Seconds</h2>
              <p className="text-base sm:text-lg lg:text-xl opacity-90 leading-relaxed">Automatically extract transactions from PDF bank statements and convert them to Tally-compatible XML, Excel, or CSV formats. Save hours of manual data entry.</p>
              <div className="flex flex-col sm:flex-row gap-4 pt-4 justify-center lg:justify-start">
                <a href="#upload" className="bg-white text-primary px-6 sm:px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition shadow-lg text-center">Get Started</a>
                <a href="#features" className="border-2 border-white text-white px-6 sm:px-8 py-3 rounded-lg font-semibold hover:bg-white hover:text-primary transition text-center">Learn More</a>
              </div>
            </div>
            <div className="flex-1 w-full max-w-sm lg:max-w-md">
              <img src="/hero.png" alt="Tally Integration" className="w-full drop-shadow-2xl rounded-lg" />
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section id="features" className="py-12 sm:py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h3 className="text-2xl sm:text-3xl font-bold text-center mb-8 sm:mb-12 text-gray-900">Why Choose Ledgerit?</h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 sm:gap-8">
            {[
              { 
                title: 'Lightning Fast', 
                desc: 'Process statements in seconds', 
                color: 'from-yellow-400 to-orange-500',
                svg: <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" /></svg>
              },
              { 
                title: 'Secure', 
                desc: 'Your data never leaves your device', 
                color: 'from-green-400 to-emerald-600',
                svg: <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" /></svg>
              },
              { 
                title: 'Multi-Bank', 
                desc: 'Supports 10+ major banks', 
                color: 'from-blue-400 to-indigo-600',
                svg: <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" /></svg>
              },
              { 
                title: 'Multiple Formats', 
                desc: 'Export to XML, Excel, CSV', 
                color: 'from-purple-400 to-pink-600',
                svg: <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" /></svg>
              },
              { 
                title: 'AI Powered', 
                desc: 'Smart extraction with machine learning', 
                color: 'from-cyan-400 to-blue-600',
                svg: <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" /></svg>
              },
              { 
                title: 'Accurate', 
                desc: '99.9% accuracy in extraction', 
                color: 'from-red-400 to-rose-600',
                svg: <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
              },
            ].map((feature, idx) => (
              <div key={idx} className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-2 border-2 border-transparent hover:border-primary group">
                <div className={`w-16 h-16 bg-gradient-to-br ${feature.color} rounded-2xl flex items-center justify-center mb-4 shadow-lg group-hover:scale-110 transition-transform duration-300`}>{feature.svg}</div>
                <h4 className="text-lg font-semibold text-gray-900 mb-2">{feature.title}</h4>
                <p className="text-gray-600 text-sm">{feature.desc}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Main Content */}
      <main id="upload" className="flex-1 max-w-7xl w-full mx-auto px-4 sm:px-6 lg:px-8 py-8 sm:py-12">
        <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
          {/* Sidebar - Workflow Steps */}
          <aside className="lg:w-64 flex-shrink-0 mb-6 lg:mb-0">
            <div className="bg-white rounded-xl shadow-lg p-3 sm:p-4 lg:p-6 lg:sticky lg:top-24">
              <h2 className="text-base sm:text-lg font-semibold text-gray-900 mb-3 sm:mb-4">Workflow</h2>
              <div className="grid grid-cols-4 lg:grid-cols-1 gap-2 sm:gap-3 lg:space-y-3 lg:space-y-4">
                {[
                  { step: 1, label: 'Upload', icon: 'ðŸ“¤', active: !file },
                  { step: 2, label: 'Preview', icon: 'ðŸ‘ï¸', active: file && !transactions.length },
                  { step: 3, label: 'Convert', icon: 'âš™ï¸', active: transactions.length > 0 },
                  { step: 4, label: 'Download', icon: 'â¬‡ï¸', active: false },
                ].map((item) => (
                  <div key={item.step} className={`flex lg:flex-row flex-col items-center lg:space-x-3 space-y-1 lg:space-y-0 p-2 sm:p-3 rounded-lg smooth-transition ${item.active ? 'bg-primary text-white shadow-md' : 'bg-gray-50 text-gray-600'}`}>
                    <span className="text-lg sm:text-xl lg:text-2xl">{item.icon}</span>
                    <div className="text-center lg:text-left">
                      <div className="text-xs sm:text-sm font-medium">{item.label}</div>
                      <div className="text-xs opacity-75 hidden lg:block">Step {item.step}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </aside>

          {/* Main Section */}
          <div className="flex-1">
            <div className="space-y-8">
            {/* Upload Area */}
            <div className="bg-white rounded-xl shadow-lg p-6 sm:p-8">
              <h2 className="text-xl sm:text-2xl font-semibold text-gray-900 mb-4">Upload Bank Statement</h2>
              <div
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
                className={`border-2 border-dashed rounded-xl p-8 sm:p-12 text-center transition ${
                  isDragging ? 'border-primary bg-blue-50 scale-105' : 'border-gray-300 hover:border-primary hover:bg-gray-50'
                }`}
              >
                <svg className="mx-auto h-12 w-12 sm:h-16 sm:w-16 text-gray-400 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
                </svg>
                <p className="text-base sm:text-lg font-medium text-gray-700 mb-2">Upload your PDF Bank Statement</p>
                <p className="text-sm text-gray-500 mb-4">Drag and drop or click to browse</p>
                <input
                  type="file"
                  accept=".pdf"
                  onChange={handleFileSelect}
                  className="hidden"
                  id="file-upload"
                  disabled={loading}
                />
                <label
                  htmlFor="file-upload"
                  className={`inline-block bg-gradient-to-r from-primary to-accent text-white px-6 py-3 rounded-lg font-medium cursor-pointer hover:shadow-lg transition ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
                >
                  {loading ? (
                    <div className="flex items-center space-x-2">
                      <div className="animate-spin rounded-full h-4 w-4 border-2 border-white border-t-transparent"></div>
                      <span>Processing...</span>
                    </div>
                  ) : 'ðŸ“ Select PDF File'}
                </label>
                {file && (
                  <div className="mt-4 text-sm text-gray-600 bg-green-50 border border-green-200 rounded-lg p-3">
                    âœ“ Selected: <span className="font-medium">{file.name}</span>
                  </div>
                )}
              </div>

              {file && showPasswordPrompt && (
                <div className="mt-4">
                  <input
                    type="password"
                    placeholder="Enter PDF Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-primary focus:border-transparent transition"
                  />
                </div>
              )}

              {file && (
                <div className="mt-4">
                  <button
                    onClick={uploadFile}
                    disabled={loading}
                    className="w-full bg-gradient-to-r from-primary to-accent text-white px-6 py-4 rounded-lg font-semibold text-lg hover:shadow-xl transition disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {loading ? 'â³ Processing...' : 'ðŸš€ Submit & Convert'}
                  </button>
                </div>
              )}

              {/* Validation Results */}
              {error && (
                <div className="mt-4 bg-red-50 border-2 border-red-200 rounded-lg p-4 flex items-start space-x-3 animate-pulse">
                  <svg className="w-5 h-5 text-red-500 mt-0.5 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                  </svg>
                  <div>
                    <h3 className="text-sm font-medium text-red-800">Validation Error</h3>
                    <p className="text-sm text-red-700 mt-1">{error}</p>
                  </div>
                </div>
              )}
            </div>

            {/* Transaction Preview Table */}
            {transactions.length > 0 && (
              <>
                <div className="relative z-30 bg-white rounded-xl shadow-lg overflow-hidden mb-6">
                  <div className="p-4 sm:p-6 border-b border-gray-200">
                    <h2 className="text-xl sm:text-2xl font-semibold text-gray-900">Transaction Preview</h2>
                    <p className="text-sm text-gray-600 mt-1">{transactions.length} transactions found from {metadata.bank_name || 'Bank'}</p>
                    {metadata.account_number && <p className="text-sm text-gray-600">Account: {metadata.account_number}</p>}
                  </div>
                  <div className="mobile-table overflow-x-auto">
                    <table className="w-full min-w-full">
                      <thead className="bg-gray-100">
                        <tr>
                          <th className="px-2 sm:px-4 lg:px-6 py-2 sm:py-3 text-left text-xs font-medium text-gray-600 uppercase tracking-wider">Date</th>
                          <th className="px-2 sm:px-4 lg:px-6 py-2 sm:py-3 text-left text-xs font-medium text-gray-600 uppercase tracking-wider">Description</th>
                          <th className="px-2 sm:px-4 lg:px-6 py-2 sm:py-3 text-right text-xs font-medium text-gray-600 uppercase tracking-wider">Debit</th>
                          <th className="px-2 sm:px-4 lg:px-6 py-2 sm:py-3 text-right text-xs font-medium text-gray-600 uppercase tracking-wider">Credit</th>
                          <th className="px-2 sm:px-4 lg:px-6 py-2 sm:py-3 text-right text-xs font-medium text-gray-600 uppercase tracking-wider">Balance</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {transactions.map((tx, idx) => (
                          <tr key={idx} className="hover:bg-blue-50 transition smooth-transition">
                            <td className="px-2 sm:px-4 lg:px-6 py-2 sm:py-4 whitespace-nowrap text-xs sm:text-sm text-gray-900">{tx.date}</td>
                            <td className="px-2 sm:px-4 lg:px-6 py-2 sm:py-4 text-xs sm:text-sm text-gray-900 max-w-xs truncate" title={tx.description}>{tx.description}</td>
                            <td className="px-2 sm:px-4 lg:px-6 py-2 sm:py-4 whitespace-nowrap text-xs sm:text-sm text-right text-red-600 font-semibold">
                              {tx.debit > 0 ? `â‚¹${tx.debit.toLocaleString()}` : '-'}
                            </td>
                            <td className="px-2 sm:px-4 lg:px-6 py-2 sm:py-4 whitespace-nowrap text-xs sm:text-sm text-right text-green-600 font-semibold">
                              {tx.credit > 0 ? `â‚¹${tx.credit.toLocaleString()}` : '-'}
                            </td>
                            <td className="px-2 sm:px-4 lg:px-6 py-2 sm:py-4 whitespace-nowrap text-xs sm:text-sm text-right text-gray-900 font-semibold">
                              â‚¹{tx.balance.toLocaleString()}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>

                {/* Conversion Actions */}
