import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Fully responsive version with mobile-first layout, collapsible navigation, and responsive table/cards

export default function App() {
  const [file, setFile] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [columns, setColumns] = useState([]);
  const [metadata, setMetadata] = useState({});
  const [error, setError] = useState('');
  const [isDragging, setIsDragging] = useState(false);
  const [loading, setLoading] = useState(false);
  const [password, setPassword] = useState('');
  const [showPasswordPrompt, setShowPasswordPrompt] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [viewMode, setViewMode] = useState('table'); // table | cards for mobile

  const handleDragOver = (e) => { e.preventDefault(); setIsDragging(true); };
  const handleDragLeave = () => { setIsDragging(false); };
  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const droppedFile = e.dataTransfer.files[0];
    if (!droppedFile) return;
    const name = droppedFile.name.toLowerCase();
    const isPDF = name.endsWith('.pdf') || droppedFile.type === 'application/pdf';
    if (isPDF) { setFile(droppedFile); setError(''); setPassword(''); setShowPasswordPrompt(false); }
    else { setError('Please upload a valid PDF file'); }
  };

  const handleFileSelect = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) return;
    if (selectedFile.size > 50 * 1024 * 1024) { setError('File size too large. Maximum 50MB allowed.'); return; }
    const name = selectedFile.name.toLowerCase();
    const isPDF = name.endsWith('.pdf') || selectedFile.type === 'application/pdf';
    if (!isPDF) { setError('Please select a valid PDF file.'); return; }
    setFile(selectedFile); setError(''); setPassword(''); setShowPasswordPrompt(false);
  };

  useEffect(() => { if (file) uploadFile(); }, [file]);

  const uploadFile = async () => {
    if (!file) return;
    setLoading(true); setError('');
    const formData = new FormData();
    formData.append('file', file);
    if (password) formData.append('password', password);
    try {
      const res = await axios.post('http://localhost:5000/upload', formData, { headers: { 'Content-Type': 'multipart/form-data' }, timeout: 120000 });
      setTransactions(res.data.transactions || []);
      setColumns(res.data.columns || []);
      setMetadata(res.data.metadata || {});
      setShowPasswordPrompt(false);
    } catch (err) {
      if (err.response?.status === 401 || err.response?.data?.error?.includes('password')) {
        setShowPasswordPrompt(true); setError('PDF is password protected. Please enter password below and submit again.');
      } else if (err.code === 'ECONNABORTED') setError('Request timed out. Please try again.');
      else if (err.code === 'ECONNREFUSED') setError('Cannot connect to server. Please ensure the API server is running on port 5000.');
      else setError(err.response?.data?.error || 'An error occurred: ' + err.message);
      setTransactions([]);
    } finally { setLoading(false); }
  };



  const PAGE_SIZE = 10;


// Existing state
const [page, setPage] = useState(0);


// Assume your existing data array is named transactions
const start = page * PAGE_SIZE;
const end = start + PAGE_SIZE;
const paginated = transactions.slice(start, end);


const totalPages = Math.ceil(transactions.length / PAGE_SIZE);


const nextPage = () => {
if (page < totalPages - 1) setPage(page + 1);
};


const prevPage = () => {
if (page > 0) setPage(page - 1);
};

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-gray-50 to-blue-50">

      {/* Header */}
      <header className="bg-white shadow-md sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-3 flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <img src="/logo.png" alt="Ledgerit Logo" className="w-12 h-12 sm:w-16 sm:h-16" />
            <h1 className="text-xl sm:text-3xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">Ledgerit</h1>
          </div>
          <nav className="hidden md:flex space-x-8">
            <a href="#home" className="text-gray-700 hover:text-blue-600 font-medium">Home</a>
            <a href="#features" className="text-gray-700 hover:text-blue-600 font-medium">Features</a>
            <a href="#support" className="text-gray-700 hover:text-blue-600 font-medium">Support</a>
          </nav>
          <button className="md:hidden p-2" onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>
            <svg className="w-7 h-7" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" /></svg>
          </button>
        </div>
        {mobileMenuOpen && (
          <div className="md:hidden border-t">
            <div className="px-4 py-2 flex flex-col space-y-2 bg-white">
              <a href="#home" onClick={() => setMobileMenuOpen(false)} className="py-2">Home</a>
              <a href="#features" onClick={() => setMobileMenuOpen(false)} className="py-2">Features</a>
              <a href="#support" onClick={() => setMobileMenuOpen(false)} className="py-2">Support</a>
            </div>
          </div>
        )}
      </header>

      {/* Hero */}
      <section className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white py-10 sm:py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 grid lg:grid-cols-2 gap-8 items-center">
          <div className="space-y-4 text-center lg:text-left">
            <h2 className="text-3xl sm:text-5xl font-bold">Convert PDF Bank Statements to Tally XML</h2>
            <p className="text-sm sm:text-lg opacity-90">Extract transactions and export into XML, Excel, or CSV formats.</p>
            <div className="flex flex-col sm:flex-row gap-3 justify-center lg:justify-start">
              <a href="#upload" className="bg-white text-blue-600 px-6 py-3 rounded-lg font-semibold">Get Started</a>
              <a href="#features" className="border-2 border-white px-6 py-3 rounded-lg font-semibold">Learn More</a>
            </div>
          </div>
          <div className="max-w-md w-full mx-auto">
            <img src="/hero.png" alt="Tally Integration" className="rounded-xl shadow-2xl w-full" />
          </div>
        </div>
      </section>

      {/* Upload & Content */}
      <main id="upload" className="flex-1 max-w-7xl mx-auto w-full px-4 sm:px-6 lg:px-8 py-6 sm:py-10">

        {/* Upload card */}
        <div className="bg-white rounded-2xl shadow-lg p-5 sm:p-8">
          <h2 className="text-xl sm:text-2xl font-semibold mb-4">Upload Bank Statement</h2>
          <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            className={`border-2 border-dashed rounded-xl p-6 sm:p-10 text-center transition ${isDragging ? 'border-blue-600 bg-blue-50' : 'border-gray-300'}`}
          >
            <input id="file-upload" type="file" accept=".pdf" onChange={handleFileSelect} className="hidden" />
            <label htmlFor="file-upload" className="cursor-pointer inline-block bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-6 py-3 rounded-lg font-medium">
              {loading ? 'Processing…' : 'Select PDF File'}
            </label>
            {file && <div className="mt-3 text-sm text-gray-700">Selected: {file.name}</div>}
          </div>

          {showPasswordPrompt && (
            <div className="mt-4 space-y-2">
              <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Enter PDF password" className="w-full border rounded-lg px-4 py-3" />
              <button disabled={!password || loading} onClick={uploadFile} className="w-full bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-4 py-3 rounded-lg">Unlock & Process</button>
            </div>
          )}

          {error && <div className="mt-4 bg-red-50 border border-red-200 text-red-700 rounded-lg p-3 text-sm">{error}</div>}
        </div>

        {/* Transactions */}
        {transactions.length > 0 && (
          <div className="mt-8 space-y-4">
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3">
              <div>
                <h3 className="text-lg sm:text-xl font-semibold">Transaction Preview</h3>
                <p className="text-sm text-gray-600">{transactions.length} transactions found</p>
              </div>
              <div className="flex gap-2 justify-end">
                <button className={`px-3 py-2 text-sm rounded-lg border ${viewMode === 'table' && 'bg-gray-100'}`} onClick={() => setViewMode('table')}>Table</button>
                <button className={`px-3 py-2 text-sm rounded-lg border ${viewMode === 'cards' && 'bg-gray-100'}`} onClick={() => setViewMode('cards')}>Cards</button>
              </div>
            </div>

            {viewMode === 'table' && (
              <div className="overflow-x-auto rounded-xl shadow">
                <table className="w-full">
                  <thead className="bg-gray-100">
                    <tr>
                      {columns.map((col, i) => (
                        <th key={i} className="px-3 py-2 text-left text-xs font-medium uppercase">{col}</th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {paginated.map((row, index) => (
                      <tr key={index}>
                        {row.map((cell, i) => (
                          <td key={i} className="px-3 py-2 border text-xs">{cell}</td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>


                {/* Pagination controls */}
                <div className="flex items-center justify-between mt-4">
                  <button
                    onClick={prevPage}
                    disabled={page === 0}
                    className="px-3 py-2 rounded-xl shadow disabled:opacity-40"
                  >
                    Previous
                  </button>


                  <span className="text-sm">
                    Page {page + 1} of {totalPages}
                  </span>


                  <button
                    onClick={nextPage}
                    disabled={page >= totalPages - 1}
                    className="px-3 py-2 rounded-xl shadow disabled:opacity-40"
                  >
                    Next
                  </button>
                </div>
              </div>
            )}

            {viewMode === 'cards' && (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                {paginated.map((row, i) => (
                  <div key={i} className="bg-white rounded-xl shadow p-4 space-y-1">
                    {columns.map((col, j) => (
                      <div key={j} className="flex justify-between text-xs sm:text-sm">
                        <span className="font-medium text-gray-600">{col}</span>
                        <span className="text-gray-900 break-all text-right max-w-[60%]">{row[j]}</span>
                      </div>
                    ))}
                  </div>
                ))}
              </div>
            )}

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">

  {/* Total Transactions */}
  <div className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-2xl p-6">
    <div className="text-sm opacity-90">Total Transactions</div>
    <div className="text-2xl font-bold">{transactions.length}</div>
  </div>

  {/* Opening Balance */}
  <div className="bg-gradient-to-r from-green-600 to-emerald-600 text-white rounded-2xl p-6">
    <div className="text-sm opacity-90">Opening Balance</div>
    <div className="text-2xl font-bold">
      {transactions.length > 0 && columns.length > 0 ? 
        `₹${transactions[0][columns.length - 1]}` : "-"}
    </div>
  </div>

  {/* Closing Balance */}
  <div className="bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-2xl p-6">
    <div className="text-sm opacity-90">Closing Balance</div>
    <div className="text-2xl font-bold">
      {transactions.length > 0 && columns.length > 0 ? 
        `₹${transactions[transactions.length - 1][columns.length - 1]}` : "-"}
    </div>
  </div>

</div>

          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 text-white mt-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 space-y-6">
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            <div>
              <h4 className="font-semibold mb-2">Ledgerit</h4>
              <p className="text-gray-400 text-sm">Convert bank statements to Tally XML effortlessly.</p>
            </div>
            <div>
              <h4 className="font-semibold mb-2">Quick Links</h4>
              <ul className="space-y-1 text-sm">
                <li><a href="#" className="text-gray-300 hover:text-white">Home</a></li>
                <li><a href="#" className="text-gray-300 hover:text-white">Features</a></li>
                <li><a href="#" className="text-gray-300 hover:text-white">Support</a></li>
              </ul>
            </div>
            <div>
              <h4 className="font-semibold mb-2">Contact</h4>
              <p className="text-gray-300 text-sm">support@ledgerit.com</p>
              <p className="text-gray-300 text-sm">+91 8777654651</p>
            </div>
          </div>
          <div className="border-t border-gray-700 pt-4 text-center text-sm text-gray-400">© 2024 Ledgerit. All rights reserved.</div>
        </div>
      </footer>
    </div>
  );
}
