import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { trackFileUpload, trackConversion, trackError } from './utils/analytics';

function App() {
  const [file, setFile] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [metadata, setMetadata] = useState({});
  const [error, setError] = useState('');
  const [isDragging, setIsDragging] = useState(false);
  const [loading, setLoading] = useState(false);
  const [password, setPassword] = useState('');
  const [showPasswordPrompt, setShowPasswordPrompt] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [recordsPerPage, setRecordsPerPage] = useState(10);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const droppedFile = e.dataTransfer.files[0];

    if (droppedFile) {
      const fileName = droppedFile.name.toLowerCase();
      const isPDF = fileName.endsWith('.pdf') || droppedFile.type === 'application/pdf';

      if (isPDF) {
        setFile(droppedFile);
        setError('');
        setPassword('');
        setShowPasswordPrompt(false);
      } else {
        setError('Please upload a valid PDF file');
      }
    }
  };

  const handleFileSelect = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (selectedFile.size > 50 * 1024 * 1024) {
        setError('File size too large. Maximum 50MB allowed.');
        return;
      }

      const fileName = selectedFile.name.toLowerCase();
      const isPDF = fileName.endsWith('.pdf') || selectedFile.type === 'application/pdf';

      if (!isPDF) {
        setError('Please select a valid PDF file.');
        return;
      }

      setFile(selectedFile);
      setError('');
      setPassword('');
      setShowPasswordPrompt(false);
    }
  };

  useEffect(() => {
    if (file) {
      uploadFile();
    }
  }, [file]);

  const uploadFile = async () => {
    if (!file) return;

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('file', file);
    if (password) formData.append('password', password);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        timeout: 30000,
      });

      console.log('Response:', response.data);

      setTransactions(response.data.transactions || []);
      setMetadata(response.data.metadata || {});
      setShowPasswordPrompt(false);

    } catch (err) {
      if (err.response?.status === 401 || err.response?.data?.error?.includes('password')) {
        setShowPasswordPrompt(true);
        setError('PDF is password protected. Please enter password below and submit again.');
      } else if (err.code === 'ECONNABORTED') {
        setError('Request timed out. Please try again.');
      } else if (err.code === 'ECONNREFUSED') {
        setError('Cannot connect to server. Please ensure the API server is running on port 5000.');
      } else {
        setError(err.response?.data?.error || 'An error occurred: ' + err.message);
      }
      setTransactions([]);
    } finally {
      setLoading(false);
    }
  };

  const handleConvertToTally = async () => {
    const bankLedger = prompt('Enter Bank Ledger Name (as in Tally):');
    if (!bankLedger) return;

    try {
      const response = await fetch('http://localhost:5000/export/tally', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ transactions, bankLedger }),
      });

      if (!response.ok) throw new Error('Export failed');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `tally_${bankLedger}_${new Date().toISOString().split('T')[0]}.xml`;
      a.click();
    } catch (err) {
      alert('Failed to export: ' + err.message);
    }
  };

  const handleExportToExcel = async () => {
